(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s;const m=()=>{let t=(()=>{let t=(new Date("25 april 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=t.hours<10?"0"+t.hours:t.hours,n.textContent=t.minutes<10?"0"+t.minutes:t.minutes,o.textContent=t.seconds<10?"0"+t.seconds:t.seconds,t.timeRemaining<=0&&(clearInterval(s),e.textContent="00",n.textContent="00",o.textContent="00")};m(),s=setInterval(m,500)})()})();